(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["ege11-home"],{"3dd3":function(n,t,e){"use strict";var i=function(){var n=this,t=n.$createElement,e=n._self._c||t;return e("b-navbar",{attrs:{type:"is-primary","mobile-burger":n.hasHomeButton}},[e("template",{slot:"brand"},[e("b-navbar-item",{attrs:{tag:"div"}},[e("p",{staticClass:"has-text-weight-bold"},[n._v("\n        "+n._s(n.title)+"\n      ")])])],1),n.hasHomeButton?e("template",{slot:"end"},[e("b-navbar-item",{attrs:{tag:"div"}},[e("b-button",{staticClass:"is-primary is-light",on:{click:function(t){return n.$router.push({name:"home"})}}},[n._v("\n        На главную\n      ")])],1)],1):n._e()],2)},s=[],a={name:"VNavbar",props:{title:{type:String,required:!0},hasHomeButton:{type:Boolean,required:!1,default:!1}}},o=a,r=e("2877"),l=Object(r["a"])(o,i,s,!1,null,"6b1eb3d1",null);t["a"]=l.exports},"61d2":function(n,t,e){"use strict";e.r(t);var i=function(){var n=this,t=n.$createElement,e=n._self._c||t;return e("div",[e("VNavbar",{attrs:{title:"ЕГЭ. Задание 11. Правописание суффиксов (кроме -Н-/-НН-)","has-home-button":""}}),e("div",{staticClass:"columns"},[e("div",{staticClass:"column"},[e("section",{staticClass:"section"},[e("div",{staticClass:"container"},[e("p",{staticClass:"title is-5"},[n._v("\n              Работа с тренажером\n            ")]),e("div",{staticClass:"content",domProps:{innerHTML:n._s(n.how_to_html)}})])]),e("section",{staticClass:"section"},[e("div",{staticClass:"container"},[e("p",{staticClass:"title is-5"},[n._v("\n              Теория\n            ")]),e("VListTheory",{attrs:{theories:n.theories}})],1)])]),e("div",{staticClass:"column"},[e("section",{staticClass:"section"},[e("div",{staticClass:"container"},[e("p",{staticClass:"title is-5"},[n._v("\n              Списки слов для закрепления\n            ")]),n._l(n.trainings_lists,(function(t,i){return e("div",{key:i,staticStyle:{"margin-top":"1.5rem"}},[e("p",{staticClass:"subtitle is-6"},[n._v(n._s(t.title))]),e("VListTraining",{attrs:{trainings:t.list}})],1)}))],2)])])])],1)},s=[],a=e("0e54"),o=e.n(a),r=e("3dd3"),l=e("7b0c"),c=function(){var n=this,t=n.$createElement,e=n._self._c||t;return e("div",n._l(n.theories,(function(t,i){return e("b-collapse",{key:"theory-"+i,staticClass:"card",attrs:{animation:"slide",open:n.openTheoryIndex===i},on:{open:function(t){n.openTheoryIndex=i}},scopedSlots:n._u([{key:"trigger",fn:function(i){return e("div",{staticClass:"card-header",attrs:{role:"button"}},[e("p",{staticClass:"card-header-title is-uppercase"},[n._v("\n        "+n._s(t.title)+"\n      ")]),e("a",{staticClass:"card-header-icon"},[e("b-icon",{attrs:{icon:i.open?"menu-down":"menu-up"}})],1)])}}],null,!0)},[e("div",{staticClass:"card-content content",domProps:{innerHTML:n._s(n.marked(t.text))}})])})),1)},u=[],p={name:"VListTheory",props:{theories:{type:Array,required:!0}},data:function(){return{openTheoryIndex:-1}},methods:{marked:function(n){return o()(n)}}},d=p,v=e("2877"),m=Object(v["a"])(d,c,u,!1,null,"4288bd2a",null),h=m.exports,g={name:"Ege11HomeView",components:{VListTheory:h,VListTraining:l["a"],VNavbar:r["a"]},data:function(){return{how_to_html:o()('Я рекомендую перед тренировкой вспомнить правила написания каждого суффикса и исключения. Если нужно, освежите знания теорией.\n\nОтветить на задание можно, нажав кнопку "Ответить" или клавишу "Enter". Тренажер покажет результат, поясняя каждую ошибку.\n\nУ каждого правила есть исключения и сложные случаи, так что лучше сделать всё. Если *время поджимает*, то хотя бы прочитайте теорию и пройдите:\n\n- *Существительные с суффиксами -ЧИК- и -ЩИК-*\n- *Глаголы с суффиксами -ОВА-, -ЕВА-, -ЫВА-, -ИВА- и -ВА*\n- *Гласные в неопределенных формах глаголов перед -ТЬ*\n- *Гласные "о" и "ё" в суффиксах и окончаниях после шипящих*'),theories:[{title:"Существительные",text:'**Суффиксы `-ИК-` и `-ЕК-`**\n\nПишется буква `и`:\n\n- если в родительном падеже буква `и` остается.\n\nПишется буква `е`:\n\n- если в родительном падеже буква `е` выпадает.\n\nПримеры:\n\n- есть карандашик - нет карандашика\n- есть человечек - нет человечка\n\n---\n\n**Суффиксы `-ИЦ-` и `-ЕЦ-`**\n\nПишется буква `и`:\n\n- в словах женского рода;\n- в словах среднего рода, ударение которых падает на слог до суффикса.\n\nПишется буква `е` (она может выпадать при склонении):\n\n- в словах мужского рода;\n- в словах среднего рода, ударение которых падает на слог после суффикса;\n- в родительном падеже множественной формы некоторых слов среднего рода с\nсуффиксом `-Ц-`.\n\nПримеры:\n\n- красавица (ж. р.)\n- красавец (м. р.)\n- бельецо (ср. р., ударение на `о`)\n- зданьице (ср. р., ударение на `а`)\n- есть блюдца - нет блюдец (ср. р. в именительном падеже суффикс `-Ц-`)\n- есть щупальца - нет щупалец (ср. р. в именительном падеже суффикс `-Ц-`)\n\n---\n\n**Суффиксы `-ИЧК-` и `-ЕЧК-`**\n\nПишется буква `и`:\n\n- если слово образовано от слова с суффиксом `-ИЦ-`;\n- если слово образовано от сокращенного мужского имени на `-ИК`.\n\nПишется буква `е` во всех остальных случаях, например:\n\n- если слово образовано от слова с суффиксом `-ЕК`;\n- если слово образовано от слова на `-МЯ`;\n- и так далее.\n\nВажно:\n\n- В русском языке нет безударного суффикса `-ЯЧК-`.\n\nПримеры:\n\n- лестничка (от лестница)\n- книжечка (от книга)\n- семечко (от семя)\n- Валечка (от Валя)\n- Эдичка (от Эдик)\n- Аличка (от Алик)\n\n---\n\n**Суффиксы `-ИНК-` и `-ЕНК-`**\n\nПишется буква `и`:\n\n- в словах, образованных от слов женского рода на `-ИНА`.\n\nПишется буква `е` во всех остальных случаях, например:\n\n- в существительных женского рода, обозначающих лиц женского пола;\n- и так далее.\n\nИсключения:\n\n- горлинка\n\nПримеры:\n\n- соломинка (от соломина)\n- француженка\n- монашенка\n\n---\n\n**Суффиксы `-ОНЬК-` и `-ЕНЬК-`**\n\nПишется буква `о`:\n\n- После твердых согласных.\n\nПишется буква `е`:\n\n- После мягких согласных;\n- После шипящих согласных;\n- После гласных.\n\nИсключения:\n\n- паинька\n- заинька\n- баиньки\n\nПримеры:\n\n- лисонька\n- тученька\n- Зоенька\n- душенька\n\n---\n\n**Суффиксы рода занятий `-ЧИК-` и `-ЩИК-`**\n\nПишется буква `ч`:\n\n- После согласных `д`-`т`, `з`-`с`, `ж`.\n\nПишется буква `щ`:\n\n- После остальных согласных;\n- После `т` в некоторых иноязычных словах, если их основа оканчивается на две согласные.\n\nВажно:\n\n- `-ЧИК-` может быть еще уменьшительно-ласкательным суффиксом, поэтому надо отличать слово "барабанщик" от слова "барабанчик".\n\nПодсказка:\n\n- Все согласные, после которых пишется буква `ч`, есть в словосочетании "ЗоСя ЖДеТ".\n\nПримеры:\n\n- перевозчик\n- разносчик\n- банщик\n- фонарщик\n- асфальтщик\n- процентщик\n\n---\n\n**Неизменяемые суффиксы**\n\nНекоторые постоянные суффиксы:\n\n- `-ИЗН-`: белизна, желтизна;\n- `-ИН-`: вышина, ширина;\n- `-ИНСТВ-`: большиство, достоинство;\n- `-ЕСТВ-`: студентчество, творчество;\n- `-ТЕЛЬ-`: учитель, строитель;\n- `-ЁР-`: комбанёр.\n\nВажно:\n\n- В суффиксе `-ИНСТВ-` обычно пишется буква `и`, но есть слово "первенство", которое пишется через букву `е`.\n\n---\n\n**Правописание слов, образованных от существительных на `-МЯ`**\n\nПишется буква `я`:\n\n- в сложных словах, содержащих в первом корне слово на `-МЯ`.\n\nПишется буква `е` во всех остальных случаях, например:\n\n- если это глагол, образованный от слова на `-МЯ`;\n- и так далее.\n\nИсключения:\n\n- знаменосец\n\nПримеры:\n\n- семядоля\n- семяпочка\n- семя - нет семени\n- пламя - над пламенем\n- бремя - обременить'},{title:"Прилагательные",text:"**Суффиксы `-ИВ-` и `-ЕВ-`**\n\nПишется буква `и`:\n\n- Если гласная в суффиксе под ударением.\n\nПишется буква `е`:\n\n- Если гласная в суффиксе безударная.\n\nИсключения:\n\n- милостивый\n- юродивый\n\nВажно:\n\n- В суффиксах `-ЛИВ-`, `-ЧИВ-` всегда пишется буква `и`, например: заботливый.\n\nПримеры:\n\n- льстивый\n- правдивый\n- боевой\n- сиреневый\n\n---\n\n**Суффиксы `-ОВ-`, `-ОВАТ-`, `-ОВИТ-` и `-ЕВ-`, `-ЕВАТ-`, `-ЕВИТ-`**\n\nПишется буква `о`:\n\n- Если суффикс стоит после твердых согласных.\n\nПишется буква `е`:\n\n- Если суффикс стоит после мягких согласных, шипящих и буквы `ц`.\n\nПримеры:\n\n- деловой\n- красноватый\n- вечевой\n- глянцевитый\n\n---\n\n**Суффиксы `-ОНЬК-` и `-ЕНЬК-`**\n\nПишется буква `о`:\n\n- После согласных `г`, `к`, `х`.\n\nПишется буква `е`:\n\n- После всех остальных согласных.\n\nПримеры:\n\n- легонький\n- дешевенький\n\n---\n\n**Неизменяемые суффиксы**\n\nНекоторые постоянные суффиксы:\n\n- `-ЛИВ-`, `-ЧИВ-`: заботливый, обманчивый;\n- `-ИСТ-`, `-ЕСК-`, `-ЧЕСК-`: надрывистый.\n- `-ЧАТ-`: створчатый, ступенчатый, решетчатый;\n- `-АТ-`, `-АН-`: дощатый."},{title:"Наречия",text:'**Суффиксы `-О-` и `-А-` наречий, образованных приставочно-суффиксальным способом**\n\nПишется буква `о`:\n\n- В наречиях с приставками `ИЗ-`, `ДО-`, `С-`.\n\nПишется буква `а`:\n\n- В наречиях с приставками `В-`, `НА-`, `ЗА-`.\n\nВажно:\n\n- Наречия могут быть образованы чисто суффиксальным способом. В таком случае, это правило не работает. Пример: досрочный + `-О-` = досрочно.\n\nПодсказка:\n\n- Можно воспользоваться правилом "окна" для проверки написания этих суффиксов. Если перед окном поставить приставку наречия, то гласная на конце слова будет совпадать с гласной, которую необходимо будет поставить в суффиксе наречия. Например, "из окна" - "изредка", "с окна" - "справа".\n\nПримеры:\n\n- изредка\n- добела\n- снова\n- вправо\n- наглухо\n- запросто'},{title:"Глаголы",text:"**Суффиксы `-ОВА-`, `-ЕВА-`, `-ЫВА-`, `-ИВА-` и `-ВА-`**\n\nПишется `-ОВА-` и `-ЕВА-`:\n\n- Если в 1-ом лице единственного числа настоящего или будущего простого времени глагол оканчивается на `-УЮ`, `-ЮЮ`.\n\nПишется `-ЫВА-` и `-ИВА-`:\n\n- Если в 1-ом лице единственного числа настоящего или будущего простого времени глагол оканчивается на `-ЫВАЮ`, `-ИВАЮ`.\n\nПишется гласная из инфинитива и суффикс `-ВА-`:\n\n- Если ударение падает на конец глагола `-ВАТЬ`, `-ВАЮ`, то в перед `-ВА-` пишется та же гласная, что и в неопределенной форме без этого суффикса.\n\nИсключения:\n\n- застрять - застревать, застреваю\n- затмить - затмевать, затмеваю\n- продлить - продлевать, продлеваю\n- и так далее\n\nВажно:\n\n- Сложный случай: разведывать (разведываю).\n\nПримеры:\n\n- заведовать (заведую)\n- исповедовать (исповедую)\n- откладывать (откладываю)\n- рассматривать (рассматриваю)\n- заливать (залить)\n- преодолевать (преодолеть)\n\n---\n\n**Гласная перед суффиксом `-Л-`**\n\nДля глаголов прошедшего времени:\n\n- Глаголы прошедшего времени образуются от основы инфинитва с сохранением той гласной, что была перед суффиксом инфинитива.\n\nВажно:\n\n- Чтобы правильно определить инфинитив, необходимо иногда смотреть на смысл глагола. Например, обессилеть от болезни - обессилить врага.\n\nПримеры:\n\n- ворочался (ворочаться)\n- слышал (слышать)\n\n---\n\n**Гласные в инфинитиве перед `-ТЬ`**\n\nТрудные глаголы в неопределенной форме:\n\n- `-АТЬ`\n\t- ворочаться\n\t- клокотать\n\t- колебать\n\t- колыхать\n\t- курлыкать\n\t- пыхать\n\t- трепетать\n- `-ЕТЬ`\n\t- выздороветь\n\t- опостылеть\n\t- опротиветь\n\t- плесневеть\n- `-ЯТЬ`\n\t- веять\n\t- затеять\n\t- каяться\n\t- лаять\n\t- лелеять\n\t- маяться\n\t- надеяться\n\t- реять\n\t- сеять\n\t- таять\n\t- чуять\n- `-ИТЬ`\n\t- драить\n\t- клеить\n\t- маячить\n\t- мерещиться\n\t- мерить\n\t- мучить\n\t- порочить\n\t- строить\n\t- успокоиться"},{title:"Прочее",text:'**Гласные `о` и `ё` в суффиксах и окончаниях после шипящих**\n\nПишется буква `о`:\n\n- В словах неотглагольного происхождения.\n\nПишется буква `ё`:\n\n- В глаголах и во всех отглагольных словах (причастиях, отглагольных прилагательных, в существительных и наречиях, образованных от глаголов и глагольных форм).\n- В заимствованных существительных с суффиксом `-ЁР-` и в русском слове "ухажёр" после шипящих корня пишется буква `ё`.\n\nИсключения:\n\n- ещё\n\nВажно:\n\nНекоторые слова исторически связаны с глаголами утратили эту связь, поэтому их правописание необходимо запомнить: трещетка, квашонка, распашонка, скачок и другие.\n\nПримеры:\n\n- лжёт\n- затушёвывать\n- сражённый\n- ночёвка\n- речонка\n- рубашонка\n- дирижёр'}],trainings_lists:[{title:"Существительные",list:[{title:"Существительные с суффиксами -ИК- и -ЕК-",topic:"ege11-nouns-ik-ek"},{title:"Существительные с суффиксами -ИЦ- и -ЕЦ-",topic:"ege11-nouns-ic-ec"},{title:"Существительные с суффиксами -ИЧК- и -ЕЧК-",topic:"ege11-nouns-ichk-echk"},{title:"Существительные с суффиксами -ИНК- и -ЕНК-",topic:"ege11-nouns-ink-enk"},{title:"Существительные с суффиксами -ОНЬК- и -ЕНЬК-",topic:"ege11-nouns-onk-enk"},{title:"Существительные с суффиксами -ЧИК- и -ЩИК-",topic:"ege11-nouns-chik-shik"},{title:"Существительные с неизменяемыми суффиксами",topic:"ege11-nouns-const"},{title:"Существительные на -МЯ",topic:"ege11-nouns-mya"}]},{title:"Прилагательные",list:[{title:"Прилагательные с суффиксами -ИВ- и -ЕВ-",topic:"ege11-adjs-iv-ev"},{title:"Прилагательные с суффиксами -ОНЬК- и -ЕНЬК-",topic:"ege11-adjs-onk-enk"},{title:"Прилагательные с суффиксами -ОВ-, -ОВАТ-, -ОВИТ- и -ЕВ-, -ЕВАТ-, -ЕВИТ-",topic:"ege11-adjs-ov-ev"},{title:"Прилагательные с неизменяемыми суффиксами",topic:"ege11-adjs-const"}]},{title:"Наречия",list:[{title:"Наречия с суффиксами -О- и -А- (приставочно-суффиксальные)",topic:"ege11-advs-o-a"}]},{title:"Глаголы",list:[{title:"Глаголы с суффиксами -ОВА-, -ЕВА-, -ЫВА-, -ИВА- и -ВА-",topic:"ege11-verbs-ova-eva-yiva-iva-va"},{title:"Гласные в глаголах перед суффиксом -Л-",topic:"ege11-verbs-before-l"},{title:"Гласные в неопределенных формах глаголов перед -ТЬ (высокая сложность)",topic:"ege11-12-verbs-infinitives-hard"},{title:"Гласные в неопределенных формах глаголов перед -ТЬ (средняя сложность)",topic:"ege11-12-verbs-infinitives-medium"}]},{title:"Прочее",list:[{title:'Гласные "о" и "ё" в суффиксах и окончаниях после шипящих',topic:"ege11-other-hsss-o-ieo"}]}]}}},b=g,_=Object(v["a"])(b,i,s,!1,null,"9e2556d2",null);t["default"]=_.exports},"7b0c":function(n,t,e){"use strict";var i=function(){var n=this,t=n.$createElement,e=n._self._c||t;return e("div",{staticClass:"list is-hoverable"},n._l(n.trainings,(function(t,i){return e("router-link",{key:"training-"+i,staticClass:"list-item",attrs:{to:{name:"training",params:{topic:t.topic}}}},[n._v("\n    "+n._s(t.title)+"\n  ")])})),1)},s=[],a={name:"VListTraining",props:{trainings:{type:Array,required:!0}}},o=a,r=e("2877"),l=Object(r["a"])(o,i,s,!1,null,"7ec7afde",null);t["a"]=l.exports}}]);
